def legendre_symbol(a, p):
    ls = pow(a, (p - 1) // 2, p)
    return -1 if ls == p - 1 else ls  

def Tonelli_Shanks(a, p):
    if legendre_symbol(a, p) != 1:
        return None
    if a == 0:
        return 0
    if p == 2:
        return a
    if p % 4 == 3:
        return pow(a, (p + 1) // 4, p)
    s = 0
    q = p - 1
    while q % 2 == 0:
        s += 1
        q //= 2
    z = 2
    while legendre_symbol(z, p) != -1:
        z += 1
    c = pow(z, q, p)
    r = pow(a, (q + 1) // 2, p)
    t = pow(a, q, p)
    m = s
    if t == 0:
        return 0
    while t != 1:
        dd = pow(t, 2, p)
        i = 0
        while dd != 1:
            dd = pow(dd, 2, p)
            i += 1
        # Calculate 2^(m-i-1)
        exp = 1
        for _ in range(m - i - 1):
            exp *= 2
        b = pow(c, exp, p)
        m = i
        r = (r * b) % p
        c = pow(b, 2, p)
        t = (t * c) % p
    return r

